tinymce.PluginManager.add("example",function(t){
	t.addButton("example",{text:"My button",icon:'backcolor',onclick:function(){t.windowManager.open({title:"Terpusat Media",body:[{type:"textbox",name:"title",label:"Title"}],onsubmit:function(e){t.insertContent("Title: "+e.data.title)}})}}),
	t.addMenuItem("example",{text:"Terpusat Media",context:"tools",onclick:function(){t.windowManager.open({title:"Terpusat Media",url:"http://localhost/terpusat/administrator/",width:800,height:500,buttons:[{text:"Close",onclick:"close"}]})}})});